INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (1,	1,	'My Current Stories',	'story.Owner_ID={User}  and story.Status<>"Done"',	'order by Iteration_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (2,	2,	'My ''Done'' Stories',	'story.Owner_ID={User}  and story.Status=''Done'' ',	'Order by Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (3,	3,	'All Work',	' 0 = (select count(*) from story as child where child.Parent_Story_ID=story.AID) ',	'order by story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (4,	4,	'All ''Done'' Work',	' story.Status=''Done'' and 0 = (select count(*) from story as child where child.Parent_Story_ID=story.AID) ',	'order by story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (5,	5,	'All Outstanding Work',	' story.Status<>''Done'' and 0 = (select count(*) from story as child where child.Parent_Story_ID=story.AID) ',	'order by story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (6,	6,	'Unsized Stories',	'story.Size=''?'' or story.Size is NULL or story.Size=''''',	'order by Iteration_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (7,	7,	'Blocked Work',	' story.Blocked<>0 ',	'Order by Iteration_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (8,	8,	'Epic Stories',	' 0 < (select count(*) from story as child where child.Parent_Story_ID=story.AID) ',	'order by story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (9,	9,	'Has Parent',	'Parent_Story_ID>0',	'order by story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (10,	10,	'NO Parent',	'Parent_Story_ID<1',	'order by story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (11,	11,	'Not in Release',	'(story.Release_ID is NULL or story.Release_ID=0) and 0 = (select count(*) from story as child where child.Parent_Story_ID=story.AID) ',	'Order by story.Status, story.Epic_Rank',	1);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (100,	100,	'Iteration Hrs',	'select  concat( initials," - " ,Friendly_Name) as User ,IF(Done=1,''Yes'',''No'') as Done , sum( Expected_Hours) as ''Est. hrs'', sum(Actual_Hours) as ''Act. hrs'' from Story left join iteration on story.Iteration_ID = Iteration.ID, Task  left JOIN user on task.User_ID = User.ID  where Story.Iteration_ID = {Iteration} and task.Story_AID = story.AID group by  User_ID, Done, Iteration_ID',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (101,	101,	'My Tasks',	'select concat( initials,\"\" - \"\" ,Friendly_Name) as User, story.ID as ''Story #'' ,task.Desc  as Task,IF(Done=0,''-'',IF(Done=1,''In Progress'',''Done'')) as Status,  Expected_Hours as ''Est. hrs'', Actual_Hours as ''Act. hrs'' from Story left join iteration on story.Iteration_ID = Iteration.ID, Task  left JOIN user on task.User_ID = User.ID  where Story.Iteration_ID = {Iteration} and User_ID = {User} and task.Story_AID = story.AID order by story.Iteration_Rank, story.ID, Done',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (102,	102,	'Iteration Tasks',	'select concat( initials,\" - \" ,Friendly_Name) as User, story.ID as ''Story #'' ,task.Desc  as Task,IF(Done=0,''-'',IF(Done=1,''In Progress'',''Done'')) as Status,  Expected_Hours as ''Est. hrs'', Actual_Hours as ''Act. hrs'' from Story left join iteration on story.Iteration_ID = Iteration.ID, Task  left JOIN user on task.User_ID = User.ID  where Story.Iteration_ID = {Iteration} and task.Story_AID = story.AID order by story.Iteration_Rank, story.ID, User_ID,  Done',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (103,	50,	'Project Progress',	'SELECT story.Type, story.Status, count(story.Type) as ''# Stories'', sum(story.Size) as ''# Points'' FROM story where story.Project_ID={Project} and ( 0 = (select count(*) from story as child where child.Parent_Story_ID=story.AID)) group by story.Type, story.Status',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (200,	500,	'Audit (recent 200)',	'SELECT * from audit order by ID desc limit 200',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (210,	510,	'Project Audit',	'SELECT * from audit where PID={Project} order by ID desc',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (220,	520,	'Org. Audit',	'SELECT * from audit where PID=0 order by ID desc',	'',	2);
INSERT INTO "queries" ("ID", "Qseq", "Desc", "QSQL", "Qorder", "External") VALUES (530,	530,	'Uploaded Files',	'select concat(''<a href=story_List.php?searchstring=%23'',story.ID,''&PID='',story.Project_ID,''&Type=search>#'',story.ID,''</a>'') as ''Story'', story.Summary as ''Summary'', upload.Desc as ''File'' , CONCAT(''<a href=upload/'',HEX(upload.Name),''.'',upload.Type,''>Link</a>'')as ''Link'' from story inner join `upload` on upload.AID=story.AID  where Story.project_ID=1 order by upload.Desc',	'',	2);
